class Product {
  final String name;
  final String imageUrl;
  final double price;
  final double mrp;
  final double weight;
  final String? pieceCount;
  final double rating;
  final int reviewCount;
  final int deliveryTime;
  final double discount;
  final bool isPremium;

  Product({
    required this.name,
    required this.imageUrl,
    required this.price,
    required this.mrp,
    required this.weight,
    this.pieceCount,
    required this.rating,
    required this.reviewCount,
    required this.deliveryTime,
    required this.discount,
    this.isPremium = false,
  });
}

class CategoryItem {
  final String image;
  final String name;
  final String id;

  CategoryItem({required this.image, required this.name, required this.id});
}

enum FilterType { dropdown, normal }

class Filter {
  String label;
  String value;
  FilterType filterType;

  Filter({
    required this.label,
    required this.value,
    required this.filterType,
  });

  bool get isDropdown => filterType == FilterType.dropdown;
}

import 'package:categories/data/data.dart';
import 'package:categories/models/category_item.dart';
import 'package:flutter/material.dart';

class CategoriesStateProvider extends ChangeNotifier {
  List<CategoryItem> _allCategories = [];
  List<CategoryItem> get allCategories => _allCategories;

  late CategoryItem _selectedCategory;
  CategoryItem get selectedCategory => _selectedCategory;

  bool isAnimating = false;

  CategoriesStateProvider() {
    _initialize();
  }

  void _initialize() async {
    _allCategories = categoryItems;
    _selectedCategory = _allCategories.first;
    notifyListeners();
  }

  void updateCategory(CategoryItem categoryItem) {
    _selectedCategory = categoryItem;
    notifyListeners();
  }

  int get getSelectedCategoryIndex => allCategories.indexOf(selectedCategory);
}
