library majestic_avatar;

import 'dart:math';

import 'package:cached_network_image/cached_network_image.dart';
import 'package:flutter/material.dart';

Color _generateRandomColor() {
  return Color.fromARGB(
    255,
    Random().nextInt(256),
    Random().nextInt(256),
    Random().nextInt(256),
  );
}

/*
RULES:

[src] can be
1. NULL - If it is null, then show background text with random color.
2. Empty - If it is empty, then show background text with random color.
3. Network URL - Fetch data from network image.
4. Asset image - Fetch data using Image.asset.

[isRound vs radius]
1. isRound and radius, either of those two params are required, not both.

[placeholder]

*/
class MajesticAvatar extends StatefulWidget {
  final String? src;
  final double radius;
  final String? text;
  final TextStyle? textStyle;
  final Color? background;
  final double? height;
  final double? width;
  final BoxFit fit;
  final bool isRound;
  final bool useOldImageOnUrlChange;

  const MajesticAvatar(
    this.src, {
    super.key,
    this.isRound = false,
    this.radius = 0,
    this.height,
    this.width,
    this.fit = BoxFit.cover,
    this.useOldImageOnUrlChange = false,
    this.text,
    this.textStyle,
    this.background,
  });

  @override
  State<MajesticAvatar> createState() => _CachedImageState();
}

class _CachedImageState extends State<MajesticAvatar> {
  Color? _color;

  @override
  void initState() {
    super.initState();
    _color = widget.background ?? _generateRandomColor();
  }

  Widget _baseBackground(String? text) {
    return Container(
      decoration: BoxDecoration(
        shape: widget.isRound ? BoxShape.circle : BoxShape.rectangle,
        color: _color,
      ),
      height: widget.height,
      width: widget.width,
      alignment: Alignment.center,
      child: text != null
          ? LayoutBuilder(
              builder: (context, constraints) {
                double fontSize =
                    min(constraints.maxWidth, constraints.maxHeight) * 0.5;
                return Text(
                  text[0].toUpperCase(),
                  style: TextStyle(
                    fontSize: fontSize,
                    fontWeight: FontWeight.w500,
                    color: _color!.computeLuminance() > .5
                        ? Colors.black
                        : Colors.white,
                  ),
                );
              },
            )
          : const SizedBox.shrink(),
    );
  }

  Widget _getChild() {
    if (widget.src == null) {
      return _baseBackground(widget.text);
    }
    return CachedNetworkImage(
      imageUrl: widget.src!,
      fit: widget.fit,
      useOldImageOnUrlChange: widget.useOldImageOnUrlChange,
      placeholder: (context, url) => _baseBackground(widget.text),
      errorWidget: (context, url, error) {
        print(error);
        return Container(
          height: widget.height,
          width: widget.width,
          color: Colors.black,
        );
      },
    );
  }

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      height: widget.height,
      width: widget.width,
      child: ClipRRect(
        borderRadius: widget.isRound
            ? BorderRadius.circular(300)
            : BorderRadius.circular(widget.radius),
        child: _getChild(),
      ),
    );
  }
}

import 'package:flutter/material.dart';
import 'package:mesh_gradient/mesh_gradient.dart';

class AnimationOptions {
  final double frequency;
  final double amplitude;
  final double speed;
  final double grain;

  const AnimationOptions({
    this.frequency = 5,
    this.amplitude = 30,
    this.speed = 2,
    this.grain = 0,
  })  : assert(grain >= 0 && grain <= 1),
        assert(speed >= 0.01 && speed <= 15);

  factory AnimationOptions.subtle() {
    return const AnimationOptions(
      frequency: 3,
      amplitude: 15,
      speed: 1,
      grain: 0.2,
    );
  }

  // Dramatic animation preset
  factory AnimationOptions.dramatic() {
    return const AnimationOptions(
      frequency: 8,
      amplitude: 45,
      speed: 4,
      grain: 0.5,
    );
  }
}

class MajesticAnimatedButton extends StatelessWidget {
  final dynamic child;
  final VoidCallback onPressed;
  final EdgeInsets padding;
  final BorderRadiusGeometry borderRadius;
  final List<Color>? colors;
  final AnimationOptions options;

  const MajesticAnimatedButton({
    super.key,
    required this.child,
    this.padding = const EdgeInsets.symmetric(horizontal: 18, vertical: 6),
    this.borderRadius = const BorderRadius.all(Radius.circular(100)),
    this.colors,
    this.options = const AnimationOptions(),
    required this.onPressed,
  }) : assert(child is Widget || child is String);

  @override
  Widget build(BuildContext context) {
    return FittedBox(
      child: GestureDetector(
        onTap: onPressed,
        child: ClipRRect(
          borderRadius: borderRadius,
          child: AnimatedMeshGradient(
            colors: colors != null && colors!.isNotEmpty
                ? colors!
                : const [
                    Colors.pink,
                    Colors.blueAccent,
                    Color.fromARGB(255, 199, 6, 6),
                    Colors.purple
                  ],
            options: AnimatedMeshGradientOptions(
              speed: options.speed,
              amplitude: options.amplitude,
              frequency: options.frequency,
              grain: options.grain,
            ),
            child: Container(
              padding: padding,
              alignment: Alignment.center,
              child: child is String
                  ? Text(
                      child,
                      style: const TextStyle(
                        color: Colors.white,
                        fontWeight: FontWeight.w600,
                        fontSize: 15,
                      ),
                    )
                  : child,
            ),
          ),
        ),
      ),
    );
  }
}

