    return VisibilityDetector(
      key: Key('video-$index'),
      onVisibilityChanged: (visibilityInfo) {
        provider.handleVisibilityChanged(index, visibilityInfo.visibleFraction);
      },
      child: Stack(
        fit: StackFit.expand,
        children: [
          _buildVideoPlayer(context),
          // _buildAudioControl(),
