import 'package:flutter/material.dart';
import 'package:flutter/services.dart';

import './widgets/app_logo.dart';
import 'models/page_item.dart';
import 'widgets/action_button.dart';
import 'widgets/mascot_and_detail_container.dart';
import 'widgets/pageview_bullets.dart';

const _splashScreenBgAnimationDuration = 800;

class WelcomePage extends StatefulWidget {
  final VoidCallback onActionButtonTap;
  const WelcomePage({super.key, required this.onActionButtonTap});

  @override
  State<WelcomePage> createState() => _WelcomePageState();
}

class _WelcomePageState extends State<WelcomePage> {
  final PageController _pageController = PageController();
  int _currentPage = 0;

  @override
  void initState() {
    super.initState();

    setIsLoggedInToFalse();

    _pageController.addListener(() {
      double maxScrollExtent = _pageController.position.maxScrollExtent;
      double scrollExtentPerSection = maxScrollExtent / 4;

      double scrollPosition = _pageController.position.pixels;

      if (scrollPosition > scrollExtentPerSection * 3) {
        setState(() {
          _currentPage = 3;
        });
      } else if (scrollPosition >
          scrollExtentPerSection + scrollExtentPerSection / 2) {
        setState(() {
          _currentPage = 2;
        });
      } else if (scrollPosition > scrollExtentPerSection / 2) {
        setState(() {
          _currentPage = 1;
        });
      } else {
        setState(() {
          _currentPage = 0;
        });
      }
    });
  }

  setIsLoggedInToFalse() async {
    // SharedPreferences prefs = await SharedPreferences.getInstance();
    // prefs.setBool('is_logged_in', false);
  }

  _setHasShownWelcomeScreenToTrue() async {
    // SharedPreferences prefs = await SharedPreferences.getInstance();
    // prefs.setBool('has_shown_welcome_screen', true);
  }

  @override
  Widget build(BuildContext context) {
    final height = MediaQuery.of(context).size.height;

    final List<PageItem> pageItemList = [
      PageItem(
        id: 'feature_one',
        title: 'Discover & Connect',
        subtitle: 'Explore new possibilities and stay connected effortlessly.',
        assetImage:
            'https://raw.githubusercontent.com/Ronak99/majestic-ui-flutter/refs/heads/master/assets/discovery.svg',
        primaryColor: const Color(0xffff725e),
      ),
      PageItem(
        id: 'feature_two',
        title: 'Flexibility at Your Fingertips',
        subtitle: 'Use the app the way you want, anytime, anywhere.',
        assetImage:
            'https://raw.githubusercontent.com/Ronak99/majestic-ui-flutter/refs/heads/master/assets/fingertips.svg',
        primaryColor: const Color(0xff92e3a9),
      ),
      PageItem(
        id: 'feature_three',
        title: 'Optimize & Grow',
        subtitle:
            'Leverage smart tools to enhance your experience and performance.',
        assetImage:
            'https://raw.githubusercontent.com/Ronak99/majestic-ui-flutter/refs/heads/master/assets/grow.svg',
        primaryColor: const Color(0xffba68c8),
      ),
      PageItem(
        id: 'feature_four',
        title: 'Seamless Transactions',
        subtitle:
            'Manage your activities with ease and get things done faster.',
        assetImage:
            'https://raw.githubusercontent.com/Ronak99/majestic-ui-flutter/refs/heads/master/assets/money.svg',
        primaryColor: const Color(0xff407bfe),
      ),
    ];

    return AnnotatedRegion<SystemUiOverlayStyle>(
      value: const SystemUiOverlayStyle(
        statusBarColor: Colors.transparent,
        statusBarIconBrightness: Brightness.dark,
        systemNavigationBarIconBrightness: Brightness.dark,
        systemNavigationBarColor: Colors.white,
      ),
      child: Scaffold(
        body: Container(
          decoration: const BoxDecoration(
            color: Colors.white,
          ),
          width: double.infinity,
          height: height,
          child: Stack(
            children: [
              AnimatedContainer(
                duration: const Duration(milliseconds: 300),
                color: pageItemList[_currentPage].primaryColor.withOpacity(.2),
              ),
              Column(
                children: [
                  Expanded(
                    child: PageView(
                      physics: const ClampingScrollPhysics(),
                      scrollBehavior: DisabledGlowBehaviour(),
                      controller: _pageController,
                      children: pageItemList
                          .map(
                            (e) => MascotAndDetailContainer(
                              pageItem: e,
                            ),
                          )
                          .toList(),
                    ),
                  ),
                  Container(
                    margin:
                        const EdgeInsets.only(left: 25, right: 25, bottom: 25),
                    child: Row(
                      children: [
                        PageViewBullets(
                          pageViewItemListLength: pageItemList.length,
                          selectedPageIndex: _currentPage,
                          primaryColor: pageItemList[_currentPage].primaryColor,
                        ),
                        const Spacer(),
                        ActionButton(
                          color: pageItemList[_currentPage].primaryColor,
                          onPressed: () {
                            if (_currentPage == 3) {
                              widget.onActionButtonTap();
                            } else {
                              _pageController.nextPage(
                                duration: const Duration(milliseconds: 300),
                                curve: Curves.easeInOut,
                              );
                            }
                          },
                        ),
                      ],
                    ),
                  ),
                ],
              ),
              const AppLogo()
            ],
          ),
        ),
      ),
    );
  }
}

class DisabledGlowBehaviour extends ScrollBehavior {
  @override
  Widget buildOverscrollIndicator(BuildContext context, Widget child, _) {
    return child;
  }
}

import 'dart:ui';

class PageItem {
  final String id;
  final String title;
  final String subtitle;
  final String assetImage;
  final Color primaryColor;

  PageItem({
    required this.id,
    required this.title,
    required this.subtitle,
    required this.assetImage,
    required this.primaryColor,
  });
}

import 'package:flutter/material.dart';

class ActionButton extends StatelessWidget {
  final Color color;
  final VoidCallback onPressed;

  const ActionButton({
    super.key,
    required this.color,
    required this.onPressed,
  });

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: onPressed,
      child: AnimatedContainer(
        duration: const Duration(milliseconds: 350),
        curve: Curves.easeInOut,
        decoration: BoxDecoration(
          shape: BoxShape.circle,
          color: color,
        ),
        padding: const EdgeInsets.all(15),
        child: const Icon(
          Icons.arrow_forward,
          color: Colors.white,
        ),
      ),
    );
  }
}

import 'package:flutter/material.dart';
import 'package:flutter_svg/flutter_svg.dart';

import '../models/page_item.dart';

class MascotAndDetailContainer extends StatelessWidget {
  final PageItem pageItem;

  const MascotAndDetailContainer({
    super.key,
    required this.pageItem,
  });

  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        SizedBox(
          height: MediaQuery.of(context).padding.top + 24,
        ),
        Flexible(
          flex: 6,
          child: Container(
            padding: const EdgeInsets.only(top: 30),
            child: SvgPicture.network(
              pageItem.assetImage,
              height: double.infinity,
            ),
          ),
        ),
        Flexible(
          flex: 4,
          child: Padding(
            padding: const EdgeInsets.only(left: 25, right: 60, top: 40),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  pageItem.title,
                  style: const TextStyle(
                    fontSize: 30,
                    fontWeight: FontWeight.w800,
                    color: Colors.black,
                  ),
                ),
                const SizedBox(height: 30),
                Text(
                  pageItem.subtitle,
                  style: const TextStyle(
                    fontSize: 17,
                    fontWeight: FontWeight.w400,
                    color: Colors.black45,
                  ),
                ),
              ],
            ),
          ),
        ),
      ],
    );
  }
}

import 'package:flutter/material.dart';

class AppLogo extends StatelessWidget {
  const AppLogo({super.key});

  @override
  Widget build(BuildContext context) {
    return Container(
      margin: EdgeInsets.only(top: MediaQuery.of(context).padding.top + 12),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          SizedBox(
            height: 35,
            width: 35,
            child: Image.network(
                'https://raw.githubusercontent.com/Ronak99/majestic-ui-flutter/refs/heads/master/assets/logo-small.png'),
          ),
          const SizedBox(width: 8),
          const Text(
            'Majestic UI',
            style: TextStyle(
              color: Colors.black,
              fontWeight: FontWeight.w700,
              fontSize: 28,
            ),
          ),
        ],
      ),
    );
  }
}

import 'package:flutter/material.dart';

class PageViewBullets extends StatelessWidget {
  final int pageViewItemListLength;
  final int selectedPageIndex;
  final Color primaryColor;

  const PageViewBullets({
    super.key,
    required this.pageViewItemListLength,
    required this.selectedPageIndex,
    required this.primaryColor,
  });

  @override
  Widget build(BuildContext context) {
    return Container(
      decoration: BoxDecoration(
        color: Colors.white,
        boxShadow: const [
          BoxShadow(
            color: Colors.black12,
            blurRadius: 2,
            offset: Offset.zero,
          )
        ],
        borderRadius: BorderRadius.circular(12),
      ),
      padding: const EdgeInsets.all(12),
      child: Row(
        children: List.generate(pageViewItemListLength, (index) => index)
            .map(
              (e) => AnimatedContainer(
                duration: const Duration(milliseconds: 100),
                margin: EdgeInsets.only(
                  right: e == pageViewItemListLength - 1 ? 0 : 8,
                ),
                decoration: BoxDecoration(
                  shape: BoxShape.circle,
                  color: e == selectedPageIndex
                      ? primaryColor
                      : const Color(0xffd9d9d9),
                ),
                height: 8,
                width: 8,
              ),
            )
            .toList(),
      ),
    );
  }
}
